import React, {useState} from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addAddVariable } from '../../../../redux/actions/newTask';

const AdditionalVariables = ({addAddVariable,additionalVariables}) => {
  const [formData, setFormData] = useState({
    variable: '',
    description: ''
  });
  const [error, setError] = useState(false);

  const onChange = e =>{
    if(e.target.name == 'variable' ){
      console.log('variable')
    }
    setFormData({...formData, [e.target.name]: e.target.value})
  };
  const onSubmit = () => {

  };
  return (
    <>
      <h5>Dodaj pomocnicze zmienne</h5>
      <form onSubmit={()=>onSubmit()}>
        <h6>Nazwa</h6>
        <input
          name={'variable'}
          placeholder="Zmienna"
          value={formData.variable}
          onChange={e => onChange(e)}
          required
        />
        <h6>Opis</h6>
        <input
          name={'description'}
          placeholder="Opis"
          value={formData.description}
          onChange={e => onChange(e)}
          required
        />
      </form>
      { error.length > 0 && error }
      <button onClick={()=>addAddVariable()}>Dodaj pomocniczą zmienną</button>
      <ul>
        {additionalVariables.length > 0 &&  additionalVariables.map(({variable, description})=>
          <li>{variable}{description}</li>
        )}
      </ul>
    </>
  )
}

AdditionalVariables.propTypes = {
}

const mapStateToProps = state => ({
  additionalVariables: state.newTask.additionalVariables,
  variables: state.newTask.variables
});

export default connect(mapStateToProps,{addAddVariable})(AdditionalVariables);
