import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import BeatLoader from "react-spinners/BeatLoader";
import MathJax from '../../MathJax';
import { connect } from 'react-redux';
import { getCloseTask } from '../../../../redux/actions/tasks';


const CloseTask = ({
  match,
  getCloseTask,
  accountType,
  tasks: {
    data,
    isFetching,
    errors,
  }
}) =>{

  useEffect(()=>{
    getCloseTask(match.params.id);
  },[]);

  const answers = {};
  const [taskStatus, setTaskStatus] =  useState(null);
  const prepareState = () => {
   data.data.forEach((item, i) => {
     answers[`answer${i}`] = false;
   });
   setTaskStatus(answers);
  }

  const onChange = (e) => {
    setTaskStatus({...taskStatus, [e.target.name]: e.target.value});
    console.log(taskStatus);
  }

  return (
    <>
    {isFetching? <BeatLoader size={20}/> :
      <>
        { taskStatus === null && prepareState()}
        <h4>{data.name}</h4>
        <ul>{data.data.map(({content},i)=>
              <li>{`${i+1}). `}
                <MathJax content={content} />
                Podaj odpowiedz:
                <input
                  name={`answer${i}`}
                  value={answers[`answer${i}`]}
                  onChange={(e)=>onChange(e)}
                ></input>
              </li>
              )
            }
        </ul>
      </>
    }
    </>
  )
}

CloseTask.propTypes = {

}

const mapStateToProps = state =>({
  tasks: state.tasks,
  accountType: state.user.data.accountType
})

export default connect(mapStateToProps,{getCloseTask})(CloseTask);
