import React, {useState} from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addAddVariable } from '../../../../redux/actions/newTask';

const AdditionalVariables = ({addAddVariable,additionalVariables}) => {
  const [formData, setFormData] = useState({
    variable: '',
    description: ''
  });
  const onChange = e => setFormData({...formData, [e.target.name]: e.target.value});
  console.log(formData);
  return (
    <>
      <h5>Dodaj pomocnicze zmienne</h5>
      <form onSubmit={()=>onSubmit()}>
        <h6>Nazwa</h6>
        <input
          placeholder="Zmienna"
          value={formData.variable}
          onChange={e => onChange(e)}
          required
        />
        <h6>Opis</h6>
        <input
          placeholder="Zmienna"
          value={formData.description}
          onChange={e => onChange(e)}
          required
        />
      </form>
      <button onClick={()=>addAddVariable()}>Dodaj pomocniczą zmienną</button>
      <ul>
        {additionalVariables.length > 0 &&  additionalVariables.map(({variable, description})=>
          <li>{variable}{description}</li>
        )}
      </ul>
    </>
  )
}

AdditionalVariables.propTypes = {
}

const mapStateToProps = state => ({
  additionalVariables: state.newTask.additionalVariables,
  variables: state.newTask.variables
});

export default connect(mapStateToProps,{addAddVariable})(AdditionalVariables);
