import axios from 'axios';
import { setAlert } from './alert';

import {
  GET_PROFILE,
  GET_PROFILES,
  PROFILE_ERROR,
  UPDATE_PROFILE,
  ACCOUNT_DELETED,
  CLEAR_PROFILE,
  GET_REPOS
} from './types';

// Get current user
export const getProfile = (accountType) => async dispatch => {
  try {
    let res;
    if(accountType == 'teacher') res = await axios.get('/api/teacher/profile/me');
    else await axios.get('/api/student/profile/me');

    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response, status: err.response }
    });
  }
};

export const getStudentProfile = () => async dispatch => {
  try {
    const res = await axios.get('/api/student/profile/me');
    console.log(res.data);
    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response, status: err.response }
    });
  }
};
