import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import BeatLoader from "react-spinners/BeatLoader";
import DisplayContent from './DisplayContent';
import { connect } from 'react-redux';
import { getOpenTask } from '../../../../redux/actions/tasks';

const getRandomIntInclusive = (min, max) => {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

const OpenTask = ({
  match,
  getOpenTask,
  tasks: { data, isFetching, errors }
}) =>{

  useEffect(()=>{
    getOpenTask(match.params.id);
  },[]);

  const [answer, setAnswer] = useState('');
  const [randomIndex, setcorrectAnswer] =
  useState(getRandomIntInclusive(0,data.data.groups.length-1));

  return(
    <>
    {isFetching? <BeatLoader size={20}/> :
      <>
        <h4>{data.name}</h4>
        <DisplayContent
          content={data.data.content}
          variables={data.data.variables}
          group={data.data.groups[randomGroupIndex]}
        />
        <input value={answer} onChange={(e)=> setAnswer(e.target.value)}></input>
        { correctAnswer == answer ? 'Brawo!' : '' }
      </>
    }
    </>
  )
}

OpenTask.propTypes = {

}

const mapStateToProps = state =>({
  tasks: state.tasks
})

export default connect(mapStateToProps,{getOpenTask})(OpenTask);
