import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getClasses } from '../../actions/classes';
import Spinner from '../layout/Spinner';
import './Classes.css';

const Classes = ({getClasses, classes, myId}) =>{
  useEffect(()=>{
    getClasses();
  },[]);

  const [students, setStudents] = useState(null);
  const [classId, setClassId] = useState(null);

  return(
    <>
      {classes == null ? <Spinner/>:
        <div className='wrapper'>
          <div className='select-class'>
            <h2>Klasy</h2>
            <ul>
              {classes.map(({title,students,_id})=><li onClick={()=>{
                setStudents(students);
                setClassId(_id);
              }}>{title}</li>)}
            </ul>
          </div>
          <div className='students-view bg-dark'>
            <h2>Uczniowie</h2>
            <ul>
              { students == null ? <h5>Wybierz klase</h5> :
                <>
                  <h5>Link do klasy</h5>
                  <h6>{`${window.location.origin}/register/${myId}/${classId}`}</h6>
                  {students.map(({student}, index)=><li>{`${index+1}. ${student.name}`}</li>)}
                </>
              }
            </ul>
          </div>
        </div>
      }
    </>
  )
}

Classes.propTypes = {
  getClasses: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
  classes: state.classes.myClasses,
  myId: state.auth.user._id
});

export default connect(mapStateToProps, {getClasses})(Classes);
