import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import {Link} from 'react-router-dom';
import BeatLoader from "react-spinners/BeatLoader";
import { connect } from 'react-redux';
import { getTasks } from '../../redux/actions/tasks';

const TasksList = ({classId,sectionId, getTasks, tasks:{ data, isFetching, errors }}) =>{

  useEffect(()=>{
    if(classId && sectionId){
      getTasks({classId,sectionId});
    }
  },[sectionId]);

  const showTaskType = (type) => {
    switch (type) {
      case 'openTask':
        return 'Zadanie otwarte'
      case 'closeTask':
        return 'Zadanie zamknięte'
      case 'booleanTask':
        return 'Zadanie prawda fałsz'
      default: return 'Zadanie'
    }
  };

  const displayTask = (id,type) => {
    switch (type) {
      case 'openTask':
        return  <Link to={`/openTask/${type}`}/>;
      case 'closeTask':
        return  <Link to={`/closeTask/${type}`}/>;
      case 'booleanTask':
        return  <Link to={`/booleanTask/${type}`}/>;
      default: return <Link to={`/tasks`}/>;
    }
  };
  return(
    <>
      {isFetching? <BeatLoader size={20}/> :
        <>
          {classId && sectionId ?
            <ul>
              {data.length> 0 ? data.map(({name, taskType, _id})=>
                <li onClick={()=>displayTask(_id,taskType)}>
                  <span>{name}    </span>
                  <span>{showTaskType(taskType)}</span>
                </li>) : 'Nie ma zadań z tego działu'}
            </ul> :
            'Wybierz dział'
          }
        </>
      }
    </>
  )
}

TasksList.propTypes = {
  classId: PropTypes.string,
  sectionId: PropTypes.string,
  getTasks: PropTypes.func,
  tasks: PropTypes.object
}

const mapStateToProps = state =>({
  tasks: state.tasks
})

export default connect(mapStateToProps,{getTasks})(TasksList);
