import {
  ADD_TASK_CONTENT
} from '../actions/newTask';

export default function reducer(statePart = [], action = {}) {
  switch (action.type) {
    case ADD_TASK_CONTENT:
      return {
        ...statePart,
        content: action.payload
      }
    default:
      return statePart;
  }
}
