import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const SendTask = ({
  newTask: {
    content,
    variables,
    model,
    groups
  }}) =>{

  const [error, setError] = useState([]);

  const sendTask = async () =>{
    try{
      setError([]);
      if(!content.length ||
         !variables.length ||
         !model.length ||
         !groups.length
      ) await setError([{msg: 'Usupełnij wszystkie pola'}]);

      let allValuesAdded = false;
      groups.forEach((item, i) => {
        allValuesAdded = variables.some(({variable}) => item[variable] == null);
        if(item.answer == null) allValuesAdded = true;
      });
      if(allValuesAdded) setError([...error,{msg: 'Nie uzupełniono wszystkich wartości w grupach'}]);

      if(error.lenght) {
        console.log('wysyłam',error.length);
      }
    } catch (e) {}
  }
  console.log(error);
  return(
    <>
      <ul>
        {error.length > 0 && error.map( ({msg}) => <li>{msg}</li> ) }
      </ul>
      <button onClick={()=>sendTask()}>Zapisz zadanie</button>
    </>
  );
};

const mapStateToProps = (state) => ({
  newTask: state.newTask
});

export default connect(mapStateToProps)(SendTask);
