import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const SendTask = ({
  newTask: {
    content,
    variables,
    model,
    groups
  }}) =>{

  const [error, setError] = useState('');

  const sendTask = () =>{

    let allValuesAdded = false;
    groups.forEach((item, i) => {
      allValuesAdded = variables.some(({variable}) => item[variable] == null);
      if(item.answer == null) allValuesAdded = true;
    });

    if(content.trim().length &&
       variables.length &&
       model.trim().length &&
       groups.length &&
       !allValuesAdded
    ) setError('');
    else setError('Coś poszło nie tak');
  }
  return(
    <>
      {error.length > 0 && error }
      <button onClick={()=>sendTask()}>Zapisz zadanie</button>
    </>
  );
};

const mapStateToProps = (state) => ({
  newTask: state.newTask
});

export default connect(mapStateToProps)(SendTask);
