import { apiRequest } from '../actions/apiRequest';
import {
  GET_TASK,
  taskSuccess,
  taskError,
  taskLoading
} from '../actions/tasks';

const configJson = {
  headers: {
    'Content-Type' : 'application/json'
  }
};

const student = ({dispatch}) => next => action => {
  next(action);
  if ( action.type === GET_TASK ) {
    dispatch(taskLoading());
    dispatch(apiRequest('GET','/api/tasks', taskSuccess, taskError, action.payload, configJson));
  }
};

export default student;
