import { apiRequest } from '../actions/apiRequest';
import {
  GET_PROFILE
} from '../actions/user';

import {
    getUser
} from '../actions/profile';


const user = ({dispatch}) => next => action => {
  next(action);
  if ( action.type === GET_PROFILE  ) {
    apiRequest()
  }
};

export default user;
