import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getUser } from '../../redux/actions/profile';
import Spinner from '../layout/Spinner';
import {Link} from 'react-router-dom';
import DashboardTeacher from './DashboardTeacher';
import DashboardStudent from './DashboardStudent';

const Dashboard = ({
  getUser,
  auth: { user, accountType },
  profile: { myProfile, loading }
}) => {
  useEffect(() => {
    getProfile(accountType);
  },[getProfile]);
  return loading && myProfile === null ? <Spinner/> :
  <>
    { accountType == 'student' ?
      <DashboardStudent user={user} myProfile={myProfile} /> :
      <DashboardTeacher user={user} myProfile={myProfile} />
    }
  </>
};

Dashboard.propTypes = {
  getTeacherProfile: PropTypes.func.isRequired,
  getStudentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  user: state.user
});

export default connect(mapStateToProps, { getProfile })(Dashboard);
