import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getOpenTask } from '../../../redux/actions/tasks';
import BeatLoader from "react-spinners/BeatLoader";

const getRandomIntInclusive = (min, max) => {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

const OpenTask = ({match, getOpenTask, tasks: { data, isFetching, errors }}) =>{
  useEffect(()=>{
    getOpenTask(match.params.id);
  },[]);
  const randomGroupIndex = getRandomIntInclusive(0,data.data.groups.length);
  console.log(randomGroupIndex);
  const displayContent = () =>{
    // for(let i in data.variables){
    //   const re = new RegExp(`{${i}}`, "g");
    //   taskContent = taskContent.replace(re, taskData[i].values[
    //     getRandomIntInclusive(0,taskData[i].values.length-1)
    //   ]);
    // }
    // return <>{taskContent}</>;
  }

  return(
    <>
    {isFetching? <BeatLoader size={20}/> :
      <>
        <h4>{data.name}</h4>
        <p>{data.data.content}</p>
      </>
    }
    </>
  )
}

OpenTask.propTypes = {

}

const mapStateToProps = state =>({
  tasks: state.tasks
})

export default connect(mapStateToProps,{getOpenTask})(OpenTask);
