import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const SendTask = ({
  newTask: {
    content,
    variables,
    model,
    groups
  }}) =>{

  const [error, setError] = useState([]);

  const sendTask = async () =>{
    try{
      await setError([]);
      console.log(content.length);
      console.log(variables.length);
      console.log(model.length);
      console.log(groups.length);

      if(!content.length){console.log('content pusty'); await setError([...error,{msg: 'Nie podano treśći zadania'}]);}
      if(!variables.length) await setError([...error,{msg: 'Nie podano zmiennych'}]);
      if(!model.length) await setError([...error,{msg: 'Nie podano wzoru'}]);
      if(!groups.length) await setError([...error,{msg: 'Nie podano grup'}]);

      let allValuesAdded = false;
      await groups.forEach((item, i) => {
        allValuesAdded = variables.some(({variable}) => item[variable] == null);
        if(item.answer == null) allValuesAdded = true;
      });
      if(allValuesAdded) await setError([...error,{msg: 'Nie uzupełniono wszystkich wartości w grupach'}]);

      if(!error.lenght) console.log('wysyłam');
    } catch (e) {
      if(e) console.log('coś poszło nie tak')
    }
  }
  console.log(error);
  return(
    <>
      <ul>
        {error.length > 0 && error.map( ({msg}) => <li>{msg}</li> ) }
      </ul>
      <button onClick={()=>sendTask()}>Zapisz zadanie</button>
    </>
  );
};

const mapStateToProps = (state) => ({
  newTask: state.newTask
});

export default connect(mapStateToProps)(SendTask);
