import axios from 'axios';
import { setAlert } from './alert';

import {
  GET_CLASSES,
  CLASSES_ERROR,
  ADD_CLASS
} from './types';

// Get teacher's classes
export const getClasses = () => async dispatch => {
  try {
    const res = await axios.get('/api/class/my');

    dispatch({
      type: GET_CLASSES,
      payload: res.data
    });
  } catch (err) {
    console.log(err);
    dispatch({
      type: CLASSES_ERROR,
      payload: err.response
    });
  }
};

export const addClass = (title) => async dispatch => {
  const config = {
    headers: {
      'Content-Type' : 'application/json'
    }
  }
  const body = JSON.stringify({title});
  try {
    const res = await axios.get('/api/class/my',body,config);
    dispatch({
      type: ADD_CLASS,
      payload: res.data
    });
  } catch (err) {
    console.log(err);
    dispatch({
      type: CLASSES_ERROR,
      payload: err.response
    });
  }
};
